import React, { useState } from "react";
import { useForm } from "react-hook-form";

import "./page-style/uploadprod.css";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

export default function UploadProd() {
  const {
    register,
    handleSubmit,
    formState: { errors },
    watch,
  } = useForm();

  async function onSubmit(data) {
    let response = await fetch("http://localhost:3006/api/v1/product", {
      method: "post",
      body: JSON.stringify({ ...data, image: "" }),
      headers: {
        "Content-Type": "application/json; charset=utf-8",
      },
    });
    const result = await response.json();
    console.log(result);
  }

  return (
    <>
      <Navbar />
      <form className="upload-form">
        <input name="prodName" className="upload-name" type="text" />
        <input name="image" className="upload-image" type="file" />
        <input name="price" className="upload-price" type="text" />
        {/* <input className="method-upload" type="text" />
        <input className="discount-upload" type="text" /> */}
        <textarea name="description" className="desc-upload" type="text" />
      </form>
      <Footer />
    </>
  );
}
