import React from "react";
import "./page-style/products.css";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import Product from "../components/Product";
import Paginate from "../components/Paginate";

export default function Products() {
  const [products, setProducts] = React.useState([]);
  const [pageCount, setPageCount] = React.useState(1);

  React.useEffect(() => {
    fetch(`http://localhost:3006/api/v1/product?page=${pageCount}&limit=${3}`)
      .then((response) => response.json())
      .then((actualData) => setProducts(actualData.data))
      .catch((err) => {
        console.log(err.message);
      });
  }, [pageCount]);
  function handlePageClick(data) {
    setPageCount((preValue) => preValue + 1);
  }

  const productsArray = products.map((product) => (
    <Product key={product.id} name={product.name} price={product.price} />
  ));

  return (
    <>
      <Navbar />
      <div className="intro-pic">
        <h1>MOBILE PHONES</h1>
        <p>BROWSE DIFFERENT PHONES FROM DIFFERENT USERS</p>
      </div>

      <div className="products">{productsArray}</div>
      <Paginate handlePageClick={handlePageClick} />
      <Footer />
    </>
  );
}
