import React from "react";
import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import "./page-style/signup.css";
import Navbar from "../components/Navbar";

export default function SignUp() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  async function submitSignup(e) {
    e.preventDefault();
    let result = await fetch("http://localhost:3006/api/v1/user/register", {
      method: "post",
      body: JSON.stringify({ ...formData, image: "culpa ea", gender: "male" }),
      headers: {
        "Content-Type": "application/json; charset=utf-8",
      },
    });
    result = await result.json();
    console.log(result);
  }

  return (
    <>
      <Navbar />
      <div className="sign-up">
        <form className="signup-form" action="">
          <h1>Register a new account.</h1>
          <div className="name-inputs">
            <input name="firstName" type="text" placeholder="First Name" />
            <input name="lastName" type="text" placeholder="Last Name" />
          </div>
          <inpu name="email" type="text" placeholder="EMAIL" />
          <inpu name="phoneNumber" type="text" placeholder="PHONE NUMBER" />
          <inpu name="password" type="password" placeholder="PASSWORD" />
          <inpu
            name="confirmPassword"
            type="password"
            placeholder="CONFIRM PASSWORD"
          />
          {/* <div className="form-gender">
            <h1 className="form-gender-header">Gender</h1>
            <div className="gender-radio">
              <label htmlFor="male">Male</label>
              <input type="radio" id="male" />
              <label htmlFor="female">Female</label>
              <input type="radio" id="female" />
            </div>
          </div> */}
          <button onClick={submitSignup}>Resgiter</button>
        </form>
        <div className="sign-in-img">
          <h1>Already registered?</h1>
          <p>Login now and welcome back!</p>
          <Link to="/signin">Login</Link>
        </div>
      </div>
    </>
  );
}
