import React from "react";
import "./page-style/profile.css";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";
import userPic from "../assets/images/test.jpg";
import UserProducts from "../components/UserProduct";

export default function Profile() {
  const [userProducts, setUserProducts] = React.useState({});
  const userData = JSON.parse(sessionStorage.getItem("user"));
  //Get the user products to be shown in profile page
  React.useEffect(() => {
    const getProductsCount = async () => {
      const userId = userData.id;
      const res = await fetch(
        `http://localhost:3006/api/v1/user/${userId}/products`
      );
      const data = await res.json();
      console.log(data.data);
      setUserProducts(data.data);
    };
    getProductsCount();
  }, []);
  return (
    <>
      <Navbar />
      <main className="container">
        <div className="user-data">
          <div className="user-pic">
            <img src={userPic} alt="user" />
          </div>
          <div className="user-data-content">
            <p className="name">
              {userData.firstName} {userData.lastName}
            </p>
            <p className="mail">{userData.email}</p>
          </div>
        </div>
        <div className="user-products">
          <p>My products</p>
          <br />
          <UserProducts />
          <UserProducts />
          <UserProducts />
          <UserProducts />
        </div>
      </main>
      <Footer />
    </>
  );
}
